mapper.select:
select *
from tblMember
where memId = #{memID}
	and memPwd = #{memPwd}

mapper.update:
update tblBoard
set
	title = #{title},
	content = #{content}
where
	idx = #{idx}

update tblBoard
set
	boardSequence = boardSequence + 1
where
	boardGroup = #{boardGroup}
	and boardSequence > #{boardSequence}
